                    ptr++;
                    c[i]--;
                }
            }
            for(int i=0;i<n;i++)
            pw.print(ans[i]+" ");

        }
        else
        {
            int ans[]=new int[n];
            int diff=n+1-sum;
            for(int i=0;i<m;i++)
            {
                for(int j=0;j<c[i];j++)
                ans[ptr++]=i+1;
            }
            if(diff<=d)
            {
                pw.println("YES");
                for(int j=0;j<n;j++)
                pw.println(ans[j]+" ");
            }
            else
            {
                pw.println("YES");
                Arrays.fill(ans,0);
                int gaps=diff/d;
                ptr=0;
                for(int i=diff%d;i<n;)
                {
                    if(ptr==m)
                    break;
                    int j;
                    for(j=i;j<i+c[ptr];j++)
                    ans[j]=ptr+1;
                    i+=c[ptr];
                    ptr++;
                    if(gaps>0)
                    {
                        i+=d;
                        gaps--;
                        i--;
                    }
                }
                for(int i=0;i<n;i++)
                pw.print(ans[i]+" ");
            }
        }
        pw.flush();
        pw.close();
    }
