void solve() {
  long long n, m, d;
  read(n, m, d);
  vector<long long> v(m);
  long long sum = 0;
  for (long long i = 0; i < m; i++) read(v[i]), sum += v[i];
  sum += ((d - 1) * (m + 1));
  if (n <= sum) {
    long long diff = sum - n;
    if (d == 1) {
      long long cnt = 1;
      cout << "YES"
           << "\n";
      for (long long i = 0; i < m; i++) {
        long long w = v[i];
        while (w--) cout << cnt << " ";
        cnt++;
      }
      cout << "\n";
    } else {
      long long times = diff / (d - 1);
      long long rem = diff % (d - 1);
      vector<vector<long long>> gaps(m + 1);
      for (long long i = 0; i < m + 1; i++) {
        for (long long j = 0; j < d - 1; j++) gaps[i].push_back(1);
      }
      long long i = 0;
      while (times--) {
        gaps[i].clear();
        i++;
      }
      while (rem--) {
        gaps[i].pop_back();
      }
      long long j = 0;
      vector<long long> ans;
      while (!gaps[j].empty()) {
        ans.push_back(0);
        gaps[j].pop_back();
      }
      j++;
      for (long long i = 0; i < m; i++) {
        while (v[i]--) ans.push_back(i + 1);
        while (!gaps[j].empty()) {
          ans.push_back(0);
          gaps[j].pop_back();
        }
        j++;
      }
      cout << "YES"
           << "\n";
      for (auto ele : ans) cout << ele << " ";
      cout << "\n";
      return;
    }
  } else
    cout << "NO"
         << "\n";
}
