long long mulmod(long long a, long long b, long long m) {
  long long res = 0;
  while (b > 0) {
    if (b & 1) res = ((m - res) > a) ? res + a : res + a - m;
    b >>= 1;
    if (b) a = ((m - a) > a) ? a + a : a + a - m;
  }
  return res;
}
long long power(long long a, long long b, long long m) {
  if (b == 0) return 1;
  if (b & 1) return mulmod(a, power(a, b - 1, m), m);
  long long tmp = power(a, b / 2, m);
  return mulmod(tmp, tmp, m);
}
bool prime(long long n) {
  for (int i = 0; i < 10; i++) {
    long long tmp = (rand() % (n - 1)) + 1;
    if (power(tmp, n - 1, n) != 1) return false;
  }
  return true;
}
long long gcd(long long a, long long b) { return b == 0 ? a : gcd(b, a % b); }
int cmp_dbl(double a, double b) {
  if (fabs(a - b) <= EPS) return 0;
  return a < b ? -1 : 1;
}
long long fastpow(long long b, long long e) {
  if (!e) return 1;
  if (e & 1) return b * fastpow(b, e - 1);
  long long x = fastpow(b, e / 2);
  return x * x;
}
long long ExtGCD(long long a, long long b, long long& x, long long& y) {
  if (!b) {
    x = 1;
    y = 0;
    return a;
  }
  long long r = ExtGCD(b, a % b, y, x);
  y -= a / b * x;
  return r;
}
