long long int pw(long long int a, long long int b) {
  long long int r = 1;
  for (long long int i = 0; i < b; i++) {
    r = ((r % 1000000007) * (a % 1000000007)) % 1000000007;
  }
  return r;
}
long long int power(long long int x, unsigned long long int y,
                    long long int p) {
  long long int res = 1;
  x = x % p;
  while (y > 0) {
    if (y & 1) res = (res * x) % p;
    y = y >> 1;
    x = (x * x) % p;
  }
  return res;
}
