            if(d <= n && d > 1) cnt = Math.min((int) (m + 1 - Math.ceil((double)temp / (d-1))),m);
            else cnt = m;
            if(m==1) {
                if(temp < d) cnt = 1;
                else cnt = 0;
            }
            int mark = 0;
            for (int i=0;i<cnt;i++){
                for(int j=0;j<arr[i];j++){
                    ans[mark] = index;
                    mark++;
                }
                index++;
            }
            while(mark <= n-1){
                for (int i=0;i<d-1;i++){
                    ans[mark] = 0;
                    mark++;
                    if(mark > n-1) break;
                }
                if(mark > n-1) break;
                for (int i=0;i<arr[cnt];i++){
                    ans[mark] = index;
                    mark++;
                }
                index++;
                cnt++;
            }
            for (int i=0;i<n;i++){
                if(i != n-1) pw.print(ans[i]+" ");
                else pw.println(ans[i]);
            }
        }

        pw.close();
