            for(int k=0;k<c[i];k++){
                a[j++]=i+1;
            }
        }
        j--;
        int count=m-1;
        int end=n;
        StringBuilder sb=new StringBuilder();
        while(count>=0 && end-j>d){
            int prev=j-c[count];
            for(int k=0;k<c[count];k++){
                a[k+1+prev]=0;
            }
            for(int k=0;k<c[count];k++){
                a[end-d-k]=count+1;

            }
            end=end-d-c[count]+1;
            j=prev;
            count--;
        }
