public class PlatformsJumping {

	static BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
	static BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(System.out));

	static int n, m, d;
	static int[] c;

	static void process() throws Exception {
		int[] ans = new int[n + 1];
		int pos = 0;
		for (int i = 0; i < m; i++) {
			pos += d;
			pos += c[i] - 1;
		}
		pos += d;
		if (pos < n + 1) {
			writer.write("NO");
			writer.newLine();
			return;
		}
		pos = 0;
		int sum = 0;
		for (int i = 0; i < m; i++) {
			sum += c[i];
		}
		for (int i = 0; i < m; i++) {
			if (pos + d + sum - 1 <= n) {
				pos += d;
			} else {
				pos = n - sum + 1;
			}
			for (int j = pos; j < pos + c[i]; j++) {
				ans[j] = i + 1;
			}
			sum -= c[i];
			pos += c[i] - 1;
		}
		writer.write("YES");
		writer.newLine();
		for (int i = 1; i <= n; i++) {
			writer.write(String.format("%d ", ans[i]));
		}
		writer.newLine();
	}

	public static void main(String[] args) throws Exception {
		//		writer = new BufferedWriter(new FileWriter("output.txt"));
		String data[], input;
		data = reader.readLine().split(" ");
		n = Integer.parseInt(data[0]); m = Integer.parseInt(data[1]);
		d = Integer.parseInt(data[2]);
		c = Arrays.asList(reader.readLine().split(" ")).stream().mapToInt(Integer::parseInt).toArray();
		process();
		writer.close();
	}

}

import java.io.IOException;
import java.io.InputStream;

public class cp1 {

    static class left_shift
    {
        public void shift(int[] arr,int n,int t)
        {
            int next=t;

            for(int i=n;i>=1;i--)
            {
                int temp=arr[i];
                arr[i]=next;

                next=temp;

                if(next==0)
                    break;
            }
        }
    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];

        private int curChar;

        private int numChars;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1)
                throw new RuntimeException();
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new RuntimeException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        public String readString() {
            final StringBuilder stringBuilder = new StringBuilder();
            int c = read();
            while (isSpaceChar(c))
                c = read();
            do {
                stringBuilder.append(c);
                c = read();
            } while (!isSpaceChar(c));

            return stringBuilder.toString();
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public long readLong() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            long res = 0;
            do {
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }
    }

    public static void main(String[] args)
    {
        InputReader inp=new cp1.InputReader(System.in);

        int n=inp.readInt();
        int m=inp.readInt();
        int d=inp.readInt();

        int plank[]=new int[m];

        for(int i=0;i<m;i++)
        {
            plank[i]=inp.readInt();
        }

        int start=d;

        for(int i=0;i<m;i++)
        {
            start+=plank[i]-1;
            start+=d;
        }

        if(start>n)
        {
            System.out.println("YES");
            int ans[]=new int[n+1];

            start=d;
            left_shift lf=new cp1.left_shift();

            for(int j=0;j<m;j++)
            {
                if(start>n)
                {
                    while (plank[j]>0)
                    {
                        lf.shift(ans,n,j+1);
                        plank[j]--;
                    }
                }
                while (plank[j] > 0)
                {
                    if (start > n) {
                        j--;
                        break;
                    }
                    ans[start] = j + 1;
                    start++;
                    plank[j]--;
                }
                start+=d-1;
            }

            StringBuilder ss=new StringBuilder();

            for(int i=1;i<=n;i++)
            {
                ss.append(ans[i]);
                ss.append(' ');
            }
            System.out.print(ss.toString());
        }
        else
        {
            System.out.println("NO");
        }
    }
}

