    void run() throws Exception {
        int n = ni();
        int m = ni();
        int d = ni();

        int[] a = new int[m];
        for(int i = 0; i < m; i++) {
            a[i] = ni();
        }

        int[] map = new int[n + 1];
        int idx = n;
        for(int i = m - 1; i >= 0; i--) {
            int num = a[i];
            while(num-- > 0) {
                map[idx--] = i + 1;
            }
        }

        //System.out.println(Arrays.toString(map));

        List<Segment> segs = new ArrayList<>();
        for(int i = 1; i <= n; i++) {
            if(map[i] == 0) {
                continue;
            }
            if(segs.isEmpty()) {
                segs.add(new Segment(map[i], i, i));
            } else {
                if(segs.get(segs.size() - 1).num == map[i]) {
                    segs.get(segs.size() - 1).right++;
                } else {
                    segs.add(new Segment(map[i], i, i));
                }
            }
        }

        //System.out.println(segs);

        int pos = d;
        for(Segment seg : segs) {
            if(pos >= n + 1) {
                break;
            } else {
                while(seg.left > pos) {
                    seg.left--;
                    seg.right--;
                }
                pos = seg.right + d;
            }
        }

        //System.out.println(segs);

        if(segs.get(segs.size() - 1).right + d < n + 1) {
            res.append("NO\n");
        } else {
            map = new int[n + 1];
            res.append("YES\n");
            for(Segment seg : segs) {
                for(int i = seg.left; i <= seg.right; i++) {
                    map[i] = seg.num;
                }
            }
            for(int i = 1; i <= n; i++) {
                res.append(map[i]).append(" ");
            }
            res.deleteCharAt(res.length() - 1).append("\n");
        }

        System.out.print(res);
    }

    class Segment {
        int num, left, right;
        public Segment(int num, int left, int right) {
            this.num   = num;
            this.left  = left;
            this.right = right; 
        }
        @Override
        public String toString() {
            return "[" + left + ", " + right + "]";
        }
    }

    //Reader & Writer
    String nextToken() throws Exception {
        if (stk == null || !stk.hasMoreTokens())
            stk = new StringTokenizer(br.readLine(), " ");
        return stk.nextToken();
    }

    String nt() throws Exception {
        return nextToken();
    }

    int ni() throws Exception {
        return Integer.parseInt(nextToken());
    }

    long nl() throws Exception {
        return Long.parseLong(nextToken());
    }

    double nd() throws Exception {
        return Double.parseDouble(nextToken());
    }

    //Some Misc methods
    int get(int l, int r, int[] a) {
        return l == 0 ? a[r] : a[r] - a[l - 1];
    }

    void shuffle(long[] a) {
        Random r = new Random();
        for(int i = 0; i < a.length; i++) {
            int idx = r.nextInt(a.length);
            long temp = a[i];
            a[i] = a[idx];
            a[idx] = temp;
        }
    }
