T mysqrt(T x) {
  if (x == 0 | x == 1) return x;
  int start = 1, End = x, ans;
  while (start <= End) {
    int mid = (start + End) / 2;
    if (mid * mid == x) return mid;
    if (mid * mid < x) {
      start = mid + 1;
      ans = mid;
    } else
      End = mid - 1;
  }
  return ans;
