/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) throws IOException {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader sc = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        Task solver = new Task();
        solver.solve(1, sc, out);
        out.close();
    }

    static class Task {
    	public ArrayList<Integer> res=new ArrayList<Integer>();
    	public int n;
    	public int m;
    	public int d;
    	public int[] c;
    	public int[] pre;
    	public boolean end=false;

    	public boolean dfs(int index,int last) {
    		if(index==m) {
    			if(last>n) 
    				return true;
    			end=true;
    			return false;
    		}

    		for(int i=Math.min(d, n);i>=1;i--) {
    			if(n-(last+i+c[index]-2)<pre[m-1]-pre[index])
    				continue;

    			res.add(last);
    			if(dfs(index+1,last+c[index]+i-1))
    				return true;
    			if(end)
    				return false;
    			res.remove(res.get(res.size()-1));
    		}
