import java.io.*;
import java.util.*;  
public class Main {

	public static void main(String[] args) throws IOException {
		BufferedReader br  =new BufferedReader(new InputStreamReader(System.in));
		PrintWriter pw = new PrintWriter(System.out);
		StringTokenizer st = new StringTokenizer(br.readLine());
		int n = Integer.parseInt(st.nextToken());
		int m = Integer.parseInt(st.nextToken());
		int d = Integer.parseInt(st.nextToken());
		st = new StringTokenizer(br.readLine());
		int[] c = new int[m];
		int sum=0;
		for (int i = 0; i < c.length; i++) {
			c[i]= Integer.parseInt(st.nextToken());
			sum+=c[i];
		}
		ArrayList<Pair> unused = new ArrayList<Pair>();
		int tmp=n-sum;
		for (int i = 0; i < c.length; i++) {
			unused.add(new Pair(tmp, tmp+c[i]-1));
			tmp=tmp+c[i];
		}
		//System.out.println(unused);
		ArrayList<Pair> used = new ArrayList<Pair>();
		int standing=-1;
		for (int i = 0; i < unused.size(); i++) {
			int maxJmp=standing + d;
			int diff=unused.get(i).l-maxJmp<0?0:unused.get(i).l-maxJmp;
			used.add(new Pair(unused.get(i).l-diff,unused.get(i).r-diff));
			standing=unused.get(i).r-diff;
		}
		//System.out.println(used);
		if(n-standing>d)
		{
			pw.println("NO");
		}
		else
		{
			pw.println("YES");
			int[] ans = new int[n];
			for (int i = 0; i < used.size(); i++) {
				for (int j = used.get(i).l; j <= used.get(i).r; j++) {
					ans[j]=i+1;
				}
			}
			for (int i = 0; i < ans.length; i++) {
				pw.print(ans[i] + " ");
			}
			pw.println();
		}
		pw.close();
	}
	static class Pair
	{
		int l,r;
		public Pair(int l, int r) {
			this.l=l;
			this.r=r;
		}
		public String toString()
		{
			return l + ", " + r;
		}
	}
}
