#include <bits/stdc++.h>
using namespace std;
const int N = 4e5 + 5;
int a[N], t[N];
int main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  cout.tie(0);
  int n;
  cin >> n;
  int ans = 0;
  for (int i = 0; i < n; i++) cin >> a[i];
  for (int k = 0; k <= 24; k++) {
    for (int i = 0; i < n; i++) t[i] = a[i] % (1 << (k + 1));
    sort(t, t + n);
    long long cnt = 0;
    for (int i = 0; i < n - 1; i++) {
      cnt += (lower_bound(t + i + 1, t + n, (1 << (k + 1)) - t[i]) -
              lower_bound(t + i + 1, t + n, (1 << k) - t[i]));
      cnt +=
          n -
          (lower_bound(t + i + 1, t + n, (1 << k) + (1 << (k + 1)) - t[i]) - t);
    }
    ans |= (1 << k) * (cnt % 2);
  }
  cout << ans << endl;
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
int main() {
  long long int n;
  cin >> n;
  vector<long long int> a(n);
  for (long long int i = 0; i < n; i++) {
    long long int x;
    cin >> x;
    a[i] = x;
  }
  sort(a.begin(), a.end());
  long long int ans = 0;
  for (long long int k = 0; k < 27; k++) {
    vector<long long int> b(n);
    for (long long int i = 0; i < n; i++) {
      b[i] = a[i] % (1 << (k + 1));
    }
    sort(b.begin(), b.end());
    long long int ctrl = 0;
    for (long long int i = 0; i < n; i++) {
      long long int l =
          lower_bound(b.begin() + i + 1, b.end(), ((1 << k) - b[i])) -
          b.begin();
      long long int r =
          lower_bound(b.begin() + i + 1, b.end(), ((1 << (k + 1)) - b[i])) -
          b.begin();
      ctrl += (r - l);
      long long int r1 = lower_bound(b.begin() + i + 1, b.end(),
                                     ((1 << k) + (1 << (k + 1)) - b[i])) -
                         b.begin();
      ctrl += (n - r1);
    }
    ans += (ctrl % 2) * 1LL * (1 << k);
  }
  cout << ans << "\n";
}

