/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author KharYusuf
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        FastReader in = new FastReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        DPresent solver = new DPresent();
        solver.solve(1, in, out);
        out.close();
    }

    static class DPresent {
        public void solve(int testNumber, FastReader s, PrintWriter w) {
            int n = s.nextInt();
            int[] a = new int[n];
            for (int i = 0; i < n; i++) a[i] = s.nextInt();
            int ans = 0;
            for (int i = 0; i < 25; i++) {
                int num = 1 << i + 1;
                long tot = 0;
                num--;
                int[] t = new int[n];
                for (int j = 0; j < n; j++) t[j] = a[j] & num;
                func.sort(t);
                int firstLowerBound = 1 << i, firstUpperBound = num, secondLowerBound = (1 << i + 1) | (1 << i);
                //w.println(secondLowerBound);
                for (int j = 0; j < n; j++) {
                    int l = func.bing(t, j + 1, n - 1, firstLowerBound - t[j]);
                    int r = func.bins(t, j + 1, n - 1, firstUpperBound - t[j]);
                    if (l <= r) tot += r - l + 1;
                    //w.print((firstLowerBound-t[j])+" "+(firstUpperBound-t[j])+" "+num+" ");
                    l = func.bing(t, j + 1, n - 1, secondLowerBound - t[j]);
                    //w.println(secondLowerBound-t[j]);
                    tot += n - l;
                }
                //w.println();
                if ((tot & 1) == 1) ans |= 1 << i;
            }
            w.println(ans);
        }

    }

    static class func {
        public static int bins(int[] a, int l, int r, int val) {
            int mid;
            while (l <= r) {
                mid = l + r >> 1;
                if (a[mid] <= val) l = mid + 1;
                else r = mid - 1;
            }
            return l - 1;
        }

        public static int bing(int[] a, int l, int r, int val) {
            int mid;
            while (l <= r) {
                mid = l + r >> 1;
                if (a[mid] >= val) r = mid - 1;
                else l = mid + 1;
            }
            return r + 1;
        }

        public static void sort(int[] arr) {
            int n = arr.length, mid, h, s, l, i, j, k;
            int[] res = new int[n];
            for (s = 1; s < n; s <<= 1) {
                for (l = 0; l < n - 1; l += (s << 1)) {
                    h = Math.min(l + (s << 1) - 1, n - 1);
                    mid = Math.min(l + s - 1, n - 1);
                    i = l;
                    j = mid + 1;
                    k = l;
                    while (i <= mid && j <= h) res[k++] = (arr[i] <= arr[j] ? arr[i++] : arr[j++]);
                    while (i <= mid) res[k++] = arr[i++];
                    while (j <= h) res[k++] = arr[j++];
                    for (k = l; k <= h; k++) arr[k] = res[k];
                }
            }
        }

    }

    static class FastReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private FastReader.SpaceCharFilter filter;

        public FastReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {

            if (numChars == -1)
                throw new InputMismatchException();

            if (curChar >= numChars) {

                curChar = 0;

                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }

                if (numChars <= 0)
                    return -1;
            }

            return buf[curChar++];
        }

        public int nextInt() {

            int c = read();

            while (isSpaceChar(c))
                c = read();

            int sgn = 1;

            if (c == '-') {
                sgn = -1;
                c = read();
            }

            int res = 0;

            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();

                res *= 10;
                res += c - '0';
                c = read();
            }
            while (!isSpaceChar(c));

            return res * sgn;
        }

        public boolean isSpaceChar(int c) {

            if (filter != null)
                return filter.isSpaceChar(c);

            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }
}

