const long long int N = 100005, M = 27;
void solve() {
  long long int i, j, k, n, m, ans = 0, cnt = 0, sum = 0;
  cin >> n;
  long long int a[n];
  for (i = 0; i < n; i++) {
    cin >> a[i];
  }
  long long int res[M] = {};
  for (j = M - 1; j >= 0; j--) {
    long long int two_pow = (1ll << (j + 1));
    two_pow -= 1;
    for (i = 0; i < n; i++) {
      a[i] = a[i] & two_pow;
    }
    sort(a, a + n);
    for (i = 0; i < n; i++) {
      long long int tot1 =
          upper_bound(a + i + 1, a + n, (1ll << (j + 2)) - 2 - a[i]) -
          lower_bound(a + i + 1, a + n, (1ll << (j + 1)) + (1ll << j) - a[i]);
      long long int tot2 =
          upper_bound(a + i + 1, a + n, (1ll << (j + 1)) - 1 - a[i]) -
          lower_bound(a + i + 1, a + n, (1ll << j) - a[i]);
      res[j] += tot1;
      res[j] += tot2;
    }
  }
  for (i = 0; i < M; i++) {
    ans += (1ll << i) * (res[i] % 2 == 1);
  }
  cout << ans;
  return;
}
int32_t main() {
  ios_base::sync_with_stdio(false);
  cin.tie(NULL);
  cout.tie(NULL);
  solve();
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
const int N = 4e5 + 5, LOG = 24;
int a[N], b[N], n, res;
int lw(int d, int i) { return lower_bound(b + 1, b + i, d) - b; }
void solve(int k) {
  for (int i = 1; i <= n; ++i) b[i] = a[i] % (1 << (k + 1));
  sort(b + 1, b + 1 + n);
  int cnt = 0;
  for (int i = 2; i <= n; ++i) {
    int c1 =
        i - lw((1 << k) - b[i], i) + 1 - (i - lw((1 << (k + 1)) - b[i], i) + 1);
    int c2 = i - lw(3 * (1 << k) - b[i], i) + 1 -
             (i - lw((1 << (k + 2)) - b[i], i) + 1);
    cnt += c1 + c2;
  }
  if (cnt & 1) res += (1 << k);
}
int main(int argc, char const *argv[]) {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  cin >> n;
  for (int i = 1; i <= n; ++i) cin >> a[i];
  for (int i = 0; i <= LOG; ++i) solve(i);
  cout << res << endl;
  return 0;
}

