import java.io.*;
import java.util.*;

public class Main {
	public static int lower_bound(int[] A, int low, int high, int x) {
		if (A[low] >= x) {
			return low;
		} else if (A[high] < x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] < x) {
				low = mid + 1;
			} else {
				high = mid - 1;
			}
		}
		return high + 1;
	}

	public static int upper_bound(int[] A, int low, int high, int x) {
		if (A[low] > x) {
			return low;
		} else if (A[high] <= x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] > x) {
				high = mid - 1;
			} else {
				low = mid + 1;
			}
		}
		return low;
	}

	public static void main(String[] args) {
		InputReader in = new InputReader();

		int N = in.nextInt();
		int[] A = new int[N];
		for (int i = 0; i < N; i++) {
			A[i] = in.nextInt();
		}
		Arrays.sort(A);
		int ans = 0;
		for (int i = 24; i >= 0; i--) {
			for (int j = 0; j < N - 1; j++) {
				int lb, ub;
				lb = lower_bound(A, j + 1, N - 1, (1 << i) - A[j]);
				ub = upper_bound(A, j + 1, N - 1, (2 << i) - 1 - A[j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
				lb = lower_bound(A, j + 1, N - 1, (3 << i) - A[j]);
				ub = upper_bound(A, j + 1, N - 1, (4 << i) - 2 - A[j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
			}
			if (i == 0) {
				break;
			}
			for (int j = 0; j < N; j++) {
				A[j] %= 1 << i;
			}
			Arrays.sort(A);
		}

		System.out.println(ans);
	}

	static class InputReader {
		public BufferedReader reader;
		public StringTokenizer st;

		public InputReader() {
			reader = new BufferedReader(new InputStreamReader(System.in));
		}

		public String next() {
			while (st == null || !st.hasMoreTokens()) {
				st = new StringTokenizer(nextLine());
			}
			return st.nextToken();
		}

		public String nextLine() {
			try {
				return reader.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			return null;
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}
	}
}

import java.io.*;
import java.util.*;
import java.math.*;

public class present
{
	static class Scan
	{
		BufferedReader br;
		StringTokenizer st;
		PrintWriter pw;
		Scan()
		{
			br = new BufferedReader(new InputStreamReader(System.in));
			pw = new PrintWriter(new OutputStreamWriter(System.out));
		}
		public int[] intarr(int size) throws IOException
		{
			setLine();
			int arr[] = new int[size];
			for (int i = 0; i < size; i++)
			{
				arr[i] = Integer.parseInt(st.nextToken());
			}
			return arr;
		}
		public ArrayList<Integer>[] arrlist(int size) throws IOException
		{
			ArrayList<Integer> arr[] = new ArrayList[size];
			for (int i = 0; i < size; i++)
			{
				setLine();
				ArrayList<Integer> input = new ArrayList<Integer>();
				while (st.hasMoreTokens())
				{
					input.add(Integer.parseInt(st.nextToken()));
				}
				arr[i] = input;
			}
			return arr;
		}
		public void setLine() throws IOException
		{
			st = new StringTokenizer(br.readLine());
		}
		public int nextInt()
		{
			return Integer.parseInt(st.nextToken());
		}
		public long nextLong()
		{
			return Long.parseLong(st.nextToken());
		}
		public String next()
		{
			return st.nextToken();
		}
		public void close() throws IOException
		{
			br.close();
			pw.close();
		}
		public void print(String s)
		{
			pw.print(s);
		}
		public void println(String s)
		{
			pw.println(s);
		}
		public void print(int s)
		{
			pw.print(s);
		}
		public void println(int s)
		{
			pw.println(s);
		}
		public void print(char s)
		{
			pw.print(s);
		}
		public void println(char s)
		{
			pw.println(s);
		}
		public void print(long s)
		{
			pw.print(s);
		}
		public void println(long s)
		{
			pw.println(s);
		}
		public void print(double s)
		{
			pw.print(s);
		}
		public void println(double s)
		{
			pw.println(s);
		}
		public void println()
		{
			pw.println();
		}
	}
	public static void main(String[] args) throws IOException
	{
		Scan s = new Scan();
		s.setLine();
		int n = s.nextInt();
		int a[] = s.intarr(n);
		int ans = 0;
		for (int i = 0; i < 26; i++)
		{
			int bits = bitsearch(a, i);
			if (bits % 2 != 0)
			{
				ans += 1 << i;
			}
		}
		s.println(ans);
		s.close();
	}
	private static int bitsearch(int[] a, int k)
	{
		int n = a.length;
		int[] temp = new int[n];
		for (int i = 0; i < n; i++)
		{
			temp[i] = a[i] % (1 << (k+1));
		}
		Arrays.sort(temp);
		int count = 0;
		int left = 1 << k;
		int right = (1 << (k + 1)) - 1;
		for (int i = 1; i < n; i++)
		{
			int low = low(i, temp, left);
			if (low == -1) continue;
			int high = high(i, temp, right);
			count += high - low + 1;
		}
		left = (1 << (k + 1)) + (1 << k);
		right = (1 << (k + 2)) - 2;
		for (int i = 1; i < n; i++)
		{
			int low = low(i, temp, left);
			if (low == -1) continue;
			int high = high(i, temp, right);
			count += high - low + 1;
		}
		return count;
	}
	private static int low(int i, int[] temp, int left)
	{
		int lo = 0;
		int hi = i - 1;

		int ans = -1;
		while (lo <= hi)
		{
			int mid = (lo + hi) >> 1;
			int sum = temp[i] + temp[mid];
			if (sum <= left - 1)
			{
				lo = mid + 1;
			} else{
				ans = mid;
				hi = mid - 1;
			}
		}
		return ans;
	}
	private static int high(int i, int[] temp, int right)
	{
		int lo = 0;
		int hi = i - 1;

		int ans = -1;
		while (lo <= hi)
		{
			int mid = (lo + hi) >> 1;
			int sum = temp[i] + temp[mid];
			if (sum < right + 1)
			{
				ans = mid;
				lo = mid + 1;
			}
			else
			{
				hi = mid - 1;
			}
		}
		return ans;
	}
}

