  cin >> n;
  for (i = 0; i < n; i++) cin >> ara[i];
  long long res = 0;
  for (i = 1; i <= 25; i++) {
    long long d = 1 << i;
    vector<long long> vec;
    for (j = 0; j < n; j++) vec.push_back(ara[j] % d);
    sort(vec.begin(), vec.end());
    long long cnt = 0;
    for (j = 0; j < n; j++) {
      if (vec[j] < (d >> 1)) {
        cnt += upper_bound(vec.begin(), vec.end(), d - vec[j] - 1) -
               lower_bound(vec.begin(), vec.end(), (d >> 1) - vec[j]);
      } else {
        cnt += lower_bound(vec.begin(), vec.end(), d - vec[j]) -
               upper_bound(vec.begin(), vec.end(), d + (d >> 1) - vec[j] - 1);
        cnt += n;
      }
      if ((vec[j] << 1) % d >= (d >> 1)) cnt--;
    }
    cnt >>= 1;
    if (cnt & 1) res += (d >> 1);
  }
  cout << res;
}

#include <bits/stdc++.h>
using namespace std;
const int N = 400010;
int a[N], b[N], n;
int get(int l, int x) {
  if (b[n] < x) return 0;
  int r = n, mid;
  while (l < r) {
    mid = l + r >> 1;
    if (b[mid] >= x)
      r = mid;
    else
      l = mid + 1;
  }
  return n - l + 1;
}
int main() {
  int i, j, m, sum, ans = 0;
  scanf("%d", &n);
  for (i = 1; i <= n; i++) scanf("%d", &a[i]);
  for (j = 0; j <= 26; j++) {
    for (i = 1; i <= n; i++) b[i] = a[i] & ((1 << j + 1) - 1);
    sort(b + 1, b + i);
    sum = 0;
    for (i = 1; i < n; i++) {
      sum = (sum + get(i + 1, (1 << j) - b[i])) % 2;
      sum = (sum - get(i + 1, (1 << j + 1) - b[i])) % 2;
      sum = (sum + get(i + 1, (1 << j) + (1 << j + 1) - b[i])) % 2;
    }
    if (sum % 2) ans |= (1 << j);
  }
  printf("%d", ans);
  return 0;
}

