bool c[(1 << 25) + 10];
int lowbit(int x) { return x & (-x); }
void add(int x) {
  while (x < v) {
    c[x] ^= 1;
    x += lowbit(x);
  }
}
bool check(int x) {
  bool res = 0;
  while (x) {
    res ^= c[x];
    x -= lowbit(x);
  }
  return res;
}
