    for (int i = 1; i <= n; i++)
      if (a[i] >> p & 1) res++;
    res = (res & 1) & ((n - res) & 1);
    for (int i = 1; i <= n; i++) {
      int pos = a[i] % v;
      if (pos) {
        res ^= check(v - 1) ^ check(v - pos - 1);
        add(pos);
      }
    }
    if (res) ans = ans | (1 << p);
  }
