using namespace std;
template <typename T>
class FenwickTree {
  int N;
  vector<T> ft;

 public:
  FenwickTree(int N) : N(N), ft(N) {}
  void update(int i, T val) {
    for (; i < N; i = (i | (i + 1))) {
      ft[i] += val;
    }
  }
  T query(int i) {
    T sum{};
    for (; i >= 0; i = (i & (i + 1)) - 1) {
      sum += ft[i];
    }
    return sum;
  }
  T query(int i, int j) { return query(j) - (i - 1 >= 0 ? query(i - 1) : 0); }
};
const int maxlog = 26;
