    for (int j = n - 1; j >= 0; j--) {
      int x = a[j] % (2 * t);
      ones += (long long)ft.query(t - x, 2 * t - x - 1);
      ones += (long long)ft.query(2 * t + t - x, 4 * t - x - 1);
      ft.update(x, 1);
    }
    if (ones % 2 == 1) ans = ans | (1 << i);
    for (auto x : a) {
      ft.update(x % (2 * t), -1);
    }
  }
