    static long getnum(int[] a ,int val) {
        long sum = 0;
        for (int i = 0; i < a.length; i++) {
            int low = i + 1;
            int hi = a.length - 1;
            int a1 = -1, a2 = -1, a3 = -1, a4 = -1;
            while (low <= hi) {
                int mid = low + hi >> 1;
                if (a[mid] + a[i] >= 2 * val) {
                    hi = mid - 1;
                } else if (a[mid] + a[i] >= val) {
                    a1 = mid;
                    hi = mid - 1;
                } else low = mid + 1;
            }
            low = i + 1;
            hi = a.length - 1;
            while (low <= hi) {
                int mid = low + hi >> 1;
                if (a[mid] + a[i] >= 2 * val) {
                    hi = mid - 1;
                } else if (a[mid] + a[i] >= val) {
                    a2 = mid;
                    low = mid + 1;
                } else low = mid + 1;
            }
            low = i+1;
            hi = a.length - 1;
            while (low <= hi) {
                int mid = low + hi >> 1;
                if (a[mid] + a[i] >= 4 * val - 1) {
                    hi = mid - 1;
                } else if (a[mid] + a[i] >= 3 * val) {
                    a3 = mid;
                    hi = mid - 1;
                } else low = mid + 1;
            }
            low = i+1;
            hi = a.length - 1;
            while (low <= hi) {
                int mid = low + hi >> 1;
                if (a[mid] + a[i] >= 4 * val - 1) {
                    hi = mid - 1;
                } else if (a[mid] + a[i] >= 3 * val) {
                    a4 = mid;
                    low = mid + 1;
                } else low = mid + 1;
            }
            if (a1 != -1) {
