    static class Task1 {
        int maxBits = 27;

        void solve(InputReader in, PrintWriter out, Logger logger) {
            int n = in.nextInt();
            int[] a = new int[n];

            for(int i=0; i<n; i++) {
                a[i] = in.nextInt();
            }
            if(isLocalEnv) logger.logln("Answer: "+bruteforce(a));

            long ans = 0;
            for (int bit=0; bit<maxBits; bit++) {
                if (bitKInFinalAns(a, bit) %2 == 1) {
                    ans += (1<<bit);
                }
            }
            out.println(ans);
        }

        long bitKInFinalAns(int[] a, int k) {
            int[] b = Arrays.stream(a).map(x -> x%(1<<(k+1))).sorted().toArray();
            long count = 0;

            for (int i=0; i<b.length; i++) {
                int curr = b[i];

                // 2^k to 2^(k+1)-1
                int start = lowerBound(b, i+1, (1 << k) - curr);
                int end = lowerBound(b, i+1, (1 << k + 1) - curr);
                if (end >= start) count += (end - start);

                // 2^(k+1)+2^k to 2^(k+2)-2
                start = lowerBound(b, i+1, (1 << k + 1) + (1 << k) - curr);
                end = lowerBound(b, i+1, (1 << k + 2) - 1 - curr);
                if (end >= start) count += (end - start);
            }

            return count;
        }

        int lowerBound(int[] a, int lo, int key) { // first element not less than key
            int hi=a.length-1;
            while(hi>=lo) {
                int mid = (lo+hi)>>1;
                if(a[mid] < key) {
                    lo = mid + 1;
                } else {
                    hi = mid - 1;
                }
            }
            return lo;
        }

        int upperBound(int[] a, int key) { // first element greater than key
            int lo=0, hi=a.length-1;
            while(hi>=lo) {
                int mid = (lo+hi)>>1;
                if(a[mid] <= key) {
                    lo = mid+1;
                } else {
                    hi = mid-1;
                }
            }
            return lo;
        }

        long bruteforce(int[] a) {
            int n = a.length;
            long result = 0;
            for(int i=0; i<n; i++) {
                for(int j=i+1; j<n; j++) {
                    result = result^(a[i]+a[j]);
                }
            }
            return result;
        }

        long fastPow(long a, long b) {
            if (a==0) return 0;
            long result = 1;
            while(b > 0) {
                if ((b&1) == 1){ result *= a; }
                a *= a;
                b >>= 1;
            }
            return result;
        }
    }

    static class Logger {
        private PrintWriter logger;
        private boolean loggingEnabled;

        void enableLogging(boolean enable, String filename) {
            this.loggingEnabled = enable;
            try {
                if (loggingEnabled) this.logger = new PrintWriter(filename);
            } catch (FileNotFoundException ignored) {
            }
        }

        void log(Object s) {
            if (loggingEnabled) {
                logger.print(s.toString());
            }
        }

        void logln(Object s) {
            if (loggingEnabled) {
                logger.println(s.toString());
            }
        }

        void close() {
            if (loggingEnabled) logger.close();
        }
    }

    static class InputReader {
        private final InputStream stream;
        private final byte[] buf = new byte[8192];
        private int curChar, numChars;
        private SpaceCharFilter filter;

        InputReader(InputStream stream) {
            this.stream = stream;
        }

        int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        int nextInt() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        long nextLong() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            long res = 0;
            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        String next() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            StringBuilder res = new StringBuilder();
            do {
                res.appendCodePoint(c);
                c = read();
            } while (!isSpaceChar(c));
            return res.toString();
        }

        boolean isSpaceChar(int c) {
            if (filter != null)
                return filter.isSpaceChar(c);
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        interface SpaceCharFilter {
            boolean isSpaceChar(int ch);
        }
    }
}

