		int[] pow = new int[27];
		for(int i = 0; i < 27; i++) {
			pow[i] = 1 << i;
		}

		int ans = 0;
		for(int k = 0; k < 25; k++) {
			int[] need = new int[n];
			for(int i = 0; i < n; i++) {
				need[i] = arr[i] % pow[k + 1];
			}
			Arrays.parallelSort(need);

			int rv = 0;
			for(int i = 0; i < n; i++) {
				int a = large(need, -need[i] + pow[k], i);
				int b = large(need, -need[i] + pow[k + 1], i);
				int c = large(need, -need[i] + pow[k + 1] + pow[k], i);
				int d = large(need, -need[i] + pow[k + 2] - 1, i);

				rv = (rv + a - b + c - d) % 2;
			}

			if(rv == 1) {
				ans ^= 1 << k;
			}
		}
		out.println(ans);
	}

	int large(int[] arr, int x, int st) {
		int l = st + 1, r = arr.length - 1, ind = arr.length;
		while(l <= r) {
			int m = (l + r) >> 1;
			if(arr[m] >= x) {
				r = m - 1;
				ind = m;
			} else {
				l = m + 1;
			}
		}
		return arr.length - ind;
	}

