      if (b[i] + b[m2] < p(k + 1))
        l2 = m2;
      else
        r2 = m2;
    }
    if (r1 != n + 1) cnt += l2 - r1 + 1;
    int l3 = i, r3 = n + 1;
    while (r3 - l3 > 1) {
