  char c = getchar();
  while (c < '0' || c > '9') {
    if (c == '-') f = -1;
    c = getchar();
  }
  while (c >= '0' && c <= '9') {
    x = (x << 3) + (x << 1) + c - '0';
    c = getchar();
  }
  return x * f;
}
int n, a[400040], cnt[20000007], ans, las;
int main() {
  n = read();
  for (int i = 1; i <= n; i++) {
    a[i] = read();
  }
  for (int k = 0; k <= 24; ++k) {
    int tmp = 0;
    for (int i = 1; i <= n; i++) {
      if ((a[i] >> k) & 1) tmp ^= 1;
    }
    if (n & 1) tmp = 0;
    ans += (tmp ^ las) << k;
    las = 0;
    int pp = 0;
    for (int j = 0; j <= k; j++) {
      for (int i = 1; i <= n; i++) {
        int c = (a[i] >> (k - j + 1) & pp);
        if (a[i] >> (k - j) & 1) ++cnt[c];
      }
      for (int i = 1; i <= n; i++) {
        int c = (a[i] >> (k - j + 1) & pp);
        if (a[i] >> (k - j) & 1) --cnt[c], c ^= pp, las ^= (cnt[c] & 1);
      }
      pp = (pp << 1 | 1);
    }
  }
  cout << ans << endl;
  return 0;
}

#include <bits/stdc++.h>
using namespace std;
const int maxn = 5e5 + 10;
long long oridata[maxn];
long long savebit[maxn];
long long temp[maxn];
int n;
long long one = 1;
long long zero = 0;
void solve() {
  long long ans = 0;
  int k = 0;
  long long base = 1;
  for (; k < 30; k++) {
    long long nowbase = base * 2;
    for (int i = 0; i < n; i++) {
      temp[i] = oridata[i] % nowbase;
    }
    sort(temp, temp + n);
    for (int i = 0; i < n; i++) {
      long long nowval = temp[i];
      long long posl =
          lower_bound(temp + i + 1, temp + n, base - nowval) - temp;
      long long posr =
          upper_bound(temp + i + 1, temp + n, nowbase - one - nowval) - temp -
          one;
      savebit[k] += max(zero, (posr - posl + one));
      posl =
          lower_bound(temp + i + 1, temp + n, nowbase + base - nowval) - temp;
      posr = upper_bound(temp + i + 1, temp + n, nowbase * 2 - one - nowval) -
             temp - one;
      savebit[k] += max(zero, (posr - posl + one));
    }
    base *= 2;
  }
}
long long getans() {
  long long nownum = 1;
  long long ans = 0;
  for (int i = 0; i < 30; i++, nownum *= 2) {
    if (savebit[i] % 2 == 1) {
      ans += nownum;
    }
  }
  return ans;
}
int main() {
  cin >> n;
  for (int i = 0; i < n; i++) {
    scanf("%lld", &oridata[i]);
  }
  solve();
  long long res = getans();
  cout << res << endl;
}

