import java.io.*;
import java.util.*;

public class Main {
	public static int lower_bound(int[] A, int low, int high, int x) {
		if (A[low] >= x) {
			return low;
		} else if (A[high] < x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] < x) {
				low = mid + 1;
			} else {
				high = mid - 1;
			}
		}
		return high + 1;
	}

	public static int upper_bound(int[] A, int low, int high, int x) {
		if (A[low] > x) {
			return low;
		} else if (A[high] <= x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] > x) {
				high = mid - 1;
			} else {
				low = mid + 1;
			}
		}
		return low;
	}

	public static void main(String[] args) {
		InputReader in = new InputReader();

		int N = in.nextInt();
		int[] A = new int[N];
		for (int i = 0; i < N; i++) {
			A[i] = in.nextInt();
		}
		int ans = 0;
		for (int i = 24; i >= 0; i--) {
			for (int j = 0; j < N; j++) {
				A[j] %= 2 << i;
			}
			Arrays.sort(A);
			for (int j = 0; j < N - 1; j++) {
				int lb, ub;
				lb = lower_bound(A, j + 1, N - 1, (1 << i) - A[j]);
				ub = upper_bound(A, j + 1, N - 1, (2 << i) - 1 - A[j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
				lb = lower_bound(A, j + 1, N - 1, (3 << i) - A[j]);
				ub = upper_bound(A, j + 1, N - 1, (4 << i) - 2 - A[j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
			}
		}

		System.out.println(ans);
	}

	static class InputReader {
		public BufferedReader reader;
		public StringTokenizer st;

		public InputReader() {
			reader = new BufferedReader(new InputStreamReader(System.in));
		}

		public String next() {
			while (st == null || !st.hasMoreTokens()) {
				st = new StringTokenizer(nextLine());
			}
			return st.nextToken();
		}

		public String nextLine() {
			try {
				return reader.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			return null;
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}
	}
}

