 		for(int j =0;j<n;j++)
 			array[j] = Integer.parseInt(st.nextToken());
 		long prod = 1;
 		long ans = 0;
 		while(prod<=20000000L){
 			prod*=2L;
 			BIT bit = new BIT((int)prod);// bit.query(i) is number that are (i-1)%prod
 			long numPairs = 0;
 			for(int j = n-1;j>=0;j--){
 				long start = ((prod/2-array[j]%prod)%prod+prod)%prod;
 				long end = prod-1-array[j]%prod;
 				if (start > end){
	 				numPairs+=bit.query((int)start+1, (int)prod);
	 				numPairs+=bit.sum((int)end+1);
 				}
 				else{
 					numPairs+=bit.query((int)start+1,  (int)end+1);
 				}
 				bit.add((int)(array[j]%prod+1),  1L);
 			}
 			if (numPairs%2 == 1)
 				ans+=(prod/2);
 		}


 		out.println(ans);

 		out.close();



 	}
	static class BIT {
        long[] tree;
        public BIT(int size) {
            tree = new long[size + 1];
        }
        public long sum(int i) {
            long ans = 0;
            while (i > 0) {
                ans += tree[i];
                i -= i & -i;
            }
            return ans;
        }

        public long query(int i, int j) {
            return sum(j) - sum(i - 1);
        }

        public void add(int i, long val) {
            while (i < tree.length) {
                tree[i] += val;
                i += i & -i;
            }
        }

        public void set(int i, long val) {
            add(i, val - query(i, i));
        }
    }
}


