#include <bits/stdc++.h>
using namespace std;
const int N = 1000010, mod = 1e9 + 7, INF = 0x3f3f3f3f;
const double eps = 1e-6;
int n;
int a[N], b[N];
int ans = 0;
int L[N], R[N], Lid[N], Rid[N], id[N];
int get(int L, int R) {
  if (L > R) return 0;
  int ans = 0;
  for (int i = n, l = 1, r = 1; i >= 1; i--) {
    while (l <= n && b[l] + b[i] < L) l++;
    while (r <= n && b[r] + b[i] <= R) r++;
    ans += r - l - (l <= i && i < r);
  }
  return ans >> 1 & 1;
}
void merge(int k) {
  int l, r;
  l = r = 0;
  for (int i = 1; i <= n; i++) {
    if (a[id[i]] >> k & 1)
      R[++r] = b[i] | (1 << k), Rid[r] = id[i];
    else
      L[++l] = b[i], Lid[l] = id[i];
  }
  int p1 = 1, p2 = 1, p = 1;
  while (p1 <= l && p2 <= r) {
    if (L[p1] <= R[p2])
      b[p] = L[p1], id[p++] = Lid[p1++];
    else
      b[p] = R[p2], id[p++] = Rid[p2++];
  }
  while (p1 <= l) b[p] = L[p1], id[p++] = Lid[p1++];
  while (p2 <= r) b[p] = R[p2], id[p++] = Rid[p2++];
}
int calc(int x) {
  merge(x);
  return (get(1 << x, (1 << (x + 1)) - 1) + get(3 << x, (1 << (x + 2)) - 2)) %
         2;
}
