import java.io.BufferedInputStream;
import java.util.Arrays;
import java.util.Scanner;

public class D {

    public static void main(String[] args) {
        Scanner in = new Scanner(new BufferedInputStream(System.in, 1 << 12));

        int n = in.nextInt();
        int[] as = new int[n];
        for (int i = 0; i < n; i++) {
            as[i] = in.nextInt();
        }

        int[] bitCounts = new int[25];
        for (int i = 0; i < as.length; i++) {
            int subj = as[i];
            for (int j = 0; j < 24; j++) {
                if ((subj & (1 << j)) > 0) {
                    ++bitCounts[j];
                }
            }
        }

        int[] carryOns = new int[25];
        for (int i = 24; i > 0; --i) {
            int bit = 1 << i;
            int mask = bit - 1;
            for (int j = 0; j < as.length; j++) {
                as[j] = as[j] & mask;
            }
            Arrays.sort(as);
            long cs = 0;
            for (int j = 0; j < as.length; j++) {
                cs += countGt(as, bit - as[j] - 1);
            }
            cs -= countGt(as, (bit >> 1) - 1);
            cs >>= 1;
            carryOns[i] = (int) cs & 1;
        }

        int result = 0;
        for (int i = 0; i <= 24; i++) {
            int bit = carryOns[i] ^ ((bitCounts[i] * as.length - bitCounts[i]) & 1);
            result ^= bit << i;
        }

//        System.out.println(Arrays.toString(carryOns));
//        System.out.println(Arrays.toString(bitCounts));
        System.out.println(result);
    }

    private static int countGt(int[] xs, int n) {
        int l = -1, r = xs.length;
        while(r - l > 1) {
            int m = (r + l) >> 1;
            if (xs[m] > n) {
                r = m;
            } else {
                l = m;
            }
        }
        return xs.length - r;
    }
}

