ll calc(vector<int> &a, int l, int r) {
  int n = a.size();
  ll ans = 0;
  vi aux;
  for (int i = 0; i < n; i++) {
    auto upp = upper_bound(aux.begin(), aux.end(), r - a[i]);
    auto lww = lower_bound(aux.begin(), aux.end(), l - a[i]);
    int up = upp - aux.begin() - 1;
    int lw = lww - aux.begin();
    if (lw < i && up < i && lw <= up) {
      ans += up - lw + 1;
    }
    aux.push_back(a[i]);
  }
  return ans;
}
