    left = powi + powi / 2;
    right = powi * 2 - 2;
    qtd += calc(amod, left, right);
    ans[i] = qtd % 2;
    powi *= 2;
  }
  ll ans1 = 0;
  for (int i = 29; i >= 0; i--) {
    ans1 = ans[i] + ans1 * 2;
