	void solve() {
		int n = scn.nextInt();
		int[] arr = scn.nextIntArray(n);

		int ans = 0;
		for(int k = 0; k < 25; k++) {
			int k2 = 1 << (k + 1);
			int[] need = new int[n];
			for(int i = 0; i < n; i++) {
				need[i] = arr[i] % k2;
			}
			Arrays.parallelSort(need);

			int rv = 0;
			for(int i = 0; i < n; i++) {
				int a = large(need, -need[i] + (1 << k), i);
				int b = large(need, -need[i] + (1 << (k + 1)), i);
				int c = large(need, -need[i] + (1 << k) + (1 << (k + 1)), i);
				int d = large(need, -need[i] + (1 << (k + 2)) - 1, i);

				rv = (rv + a - b + c - d) % 2;
			}

			if(rv == 1) {
				ans ^= 1 << k;
			}
		}
		out.println(ans);
	}

	int large(int[] arr, int x, int st) {
		int l = st + 1, r = arr.length - 1, ind = arr.length;
		while(l <= r) {
			int m = (l + r) >> 1;
			if(arr[m] >= x) {
				r = m - 1;
				ind = m;
			} else {
				l = m + 1;
			}
		}
		return arr.length - ind;
	}

