public class Main {
	public static int lower_bound(int[] A, int low, int high, int x) {
		if (A[low] >= x) {
			return low;
		} else if (A[high] < x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] < x) {
				low = mid + 1;
			} else {
				high = mid - 1;
			}
		}
		return high + 1;
	}

	public static int upper_bound(int[] A, int low, int high, int x) {
		if (A[low] > x) {
			return low;
		} else if (A[high] <= x) {
			return high + 1;
		}
		while (low <= high) {
			int mid = (low + high) / 2;
			if (A[mid] > x) {
				high = mid - 1;
			} else {
				low = mid + 1;
			}
		}
		return low;
	}

	public static void main(String[] args) {
		InputReader in = new InputReader();

		int N = in.nextInt();
		int[] A = new int[N];
		for (int i = 0; i < N; i++) {
			A[i] = in.nextInt();
		}
		int[][] B = new int[25][N];
		for (int i = 0; i < 25; i++) {
			for (int j = 0; j < N; j++) {
				B[i][j] = A[j] % (2 << i);
			}
			Arrays.sort(B[i]);
		}
		int ans = 0;
		for (int i = 0; i < 25; i++) {
			for (int j = 0; j < N - 1; j++) {
				int lb, ub;
				lb = lower_bound(B[i], j + 1, N - 1, (1 << i) - B[i][j]);
				ub = upper_bound(B[i], j + 1, N - 1, (2 << i) - 1 - B[i][j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
				lb = lower_bound(B[i], j + 1, N - 1, (3 << i) - B[i][j]);
				ub = upper_bound(B[i], j + 1, N - 1, (4 << i) - 2 - B[i][j]);
				ans ^= ((Math.max(ub - lb, 0)) % 2) << i;
			}
		}

		System.out.println(ans);
	}

