using namespace std;
long long CountFrec(vector<int>& v, int vmin, int vmax) {
  long long ans = 0;
  for (int i = 1; i < v.size(); i++) {
    int from = vmin - v[i];
    int to = vmax - v[i];
    if (from > to) continue;
    int st = lower_bound(v.begin(), v.begin() + i, from) - v.begin();
    int dr = upper_bound(v.begin(), v.begin() + i, to) - v.begin();
    ans += dr - st;
  }
  return ans;
}
