    auto elem = v;
    for (auto& i : elem) i &= (2 << b) - 1;
    sort(elem.begin(), elem.end());
    long long nr = CountFrec(elem, 1 << b, (2 << b) - 1);
    nr += CountFrec(elem, 3 << b, (4 << b) - 1);
    if (nr & 1LL) ans += (1 << b);
  }
  cout << ans << '\n';
  return 0;
}

