import java.util.Arrays;
import java.util.Scanner;

public class R626_D {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		int[] a = new int[n];
		for(int i = 0; i < n; i++) {
			a[i] = in.nextInt();
		}
		int ans = 0;
		for(int k = 0; k < 26; k++) {
			int kBits = countKBits(a, k);
			if (kBits % 2 != 0) {
				ans += 1 << k;
			}
		}
		System.out.println(ans);

		in.close();
	}

	private static int countKBits(int[] a, int k) {
		int n = a.length;
		int[] temp = new int[n];
		for (int i = 0; i < n; i++) {
			temp[i] = a[i] % (1 << (k+1));
		}
		Arrays.sort(temp);
		int count = 0;
		int left = 1 << k;
		int right = (1 << (k + 1)) - 1;

		for (int i = 1; i < n; i++) {
			int lower = findLower(i, temp, left, right);
			if (lower == -1) continue;
			int upper = findUpper(i, temp, left, right);
			count += upper - lower + 1;
		}

		left = (1 << (k + 1)) + (1 << k);
		right = (1 << (k + 2)) - 2;
		for (int i = 1; i < n; i++) {
			int lower = findLower(i, temp, left, right);
			if (lower == -1) continue;
			int upper = findUpper(i, temp, left, right);
			count += upper - lower + 1;
		}
		return count;
	}

	private static int findLower(int i, int[] temp, int left, int right) {
		int lo = 0;
		int hi = i - 1;

		int ans = -1;
		while (lo <= hi) {
			int mid = (lo + hi) >> 1;
			int sum = temp[i] + temp[mid];
			if (sum < left) {
				lo = mid + 1;
			} else {
				if (sum > right) {
					hi = mid - 1; 
				} else {
					ans = mid;
					hi = mid - 1;
				}
			}
		}
		return ans;
	}
	private static int findUpper(int i, int[] temp, int left, int right) {
		int lo = 0;
		int hi = i - 1;

		int ans = -1;
		while (lo <= hi) {
			int mid = (lo + hi) >> 1;
			int sum = temp[i] + temp[mid];
			if (sum < left) {
				lo = mid + 1;
			} else {
				if (sum > right) {
					hi = mid - 1; 
				} else {
					ans = mid;
					lo = mid + 1;
				} 
			}
		}
		return ans;
	}
}

