long long f(vector<int> &b, int limit) {
  long long res = 0;
  int l = 0, r = n - 1;
  while (l <= r) {
    while (l <= r && b[r] + b[l] > limit) r--;
    if (l > r) break;
    res += r - l;
    l++;
  }
  return res;
}
long long solve(vector<int> &b, int L, int R) { return f(b, R) - f(b, L - 1); }
