#include <bits/stdc++.h>
using namespace std;
const double PI = acos(-1.0);
const double EPS = 1e-9;
const long long MOD = 1e9 + 7;
const long long INF = 0x3f3f3f3f;
const long long MAXN = 4e5 + 7;
int n, a[MAXN];
int cnt[2], dp[MAXN];
long long ans;
void solve() {
  cin >> n;
  for (int i = 1; i <= n; ++i) cin >> a[i];
  int BASE = 0;
  for (int i = 0; i <= 25; ++i) {
    BASE = (1 << (i + 1)) - 1;
    for (int j = 1; j <= n; ++j) dp[j] = a[j] & BASE;
    sort(dp + 1, dp + n + 1);
    int t1 = n, t2 = n, t3 = n, t4 = n;
    long long res = 0;
    for (int k, j = 1; j <= n; ++j) {
      k = (1 << i) * 1 - 1 - dp[j];
      while (t1 && dp[t1] > k) --t1;
      k = (1 << i) * 2 - 1 - dp[j];
      while (t2 && dp[t2] > k) --t2;
      k = (1 << i) * 3 - 1 - dp[j];
      while (t3 && dp[t3] > k) --t3;
      k = (1 << i) * 4 - 1 - dp[j];
      while (t4 && dp[t4] > k) --t4;
      k = (t2 - t1) + (t4 - t3) - ((t1 < j && j <= t2) || (t3 < j && j <= t4));
      res += k;
    }
    ans |= (1ll << i) * (res >> 1 & 1);
  }
  cout << ans << endl;
}
