                array[b] = new answer(b,a);

            }
            for (int i = 0; i < n; i++) {
                if(array[i]==null)
                {
                    array[i] = new answer(0, 0);
                    c++;
                }
            }


            Arrays.sort(array);


            if(e==0){
                if(k>0)
                    out.println(1+2*(k-1));
                else
