using namespace std;
const int inf = (int)1e9;
const int mod = (int)1e9 + 7;
vector<int> g[200200];
void solve() {
  int n, k;
  scanf("%d%d\n", &n, &k);
  vector<char> A(n);
  for (int i = 0; i < n; i++) {
    scanf("%c", &A[i]);
  }
  scanf("\n");
  for (int i = 0; i <= n; i++) g[i].clear();
  int last1 = -1;
  for (int i = 0; i < n; i++) {
    if (A[i] == 'W') {
      if (last1 != -1) {
        int len = i - last1;
        if (len > 0) {
          g[len].push_back(i);
        }
      }
      last1 = i;
    }
  }
  vector<int> order;
  for (int i = 1; i <= n; i++) {
    for (int j = 0; j < g[i].size(); j++) {
      order.push_back(g[i][j]);
    }
  }
  for (int i = 0; i < n; i++) {
    if (A[i] == 'W') {
      order.push_back(i);
      break;
    }
  }
  for (int i = 0; i < order.size(); i++) {
    int pos = order[i];
    int q = pos - 1;
    while (k > 0 && q >= 0 && A[q] != 'W') {
      A[q] = 'W';
      k--;
      q--;
    }
  }
  bool hasSeen1 = false;
  for (int i = 0; i < n; i++) {
    if (A[i] == 'W') {
      hasSeen1 = true;
    }
    if (A[i] == 'L' && hasSeen1 && k > 0) {
      A[i] = 'W';
      k--;
    }
  }
  for (int i = 0; i < n; i++) {
    if (A[i] == 'L' && k > 0) {
      A[i] = 'W';
      k--;
    }
  }
  int ans = 0;
  for (int i = 0; i < n; i++) {
    if (A[i] == 'W') {
      if (i >= 1 && A[i - 1] == 'W')
        ans += 2;
      else
        ans += 1;
    }
  }
  cout << ans << endl;
}
int main() {
  int t;
  scanf("%d", &t);
  for (int i = 0; i < t; i++) {
    solve();
  }
  return 0;
}

