	void main() {

		int t=inp.nextInt();
		while(t-->0) {
			int n=inp.nextInt();
			int k=inp.nextInt();
			char s[]=inp.next().toCharArray();
			ArrayList<Integer> starts=new ArrayList<>();
			for(int i=1;i<n;i++) {
				if(s[i]=='L' && s[i-1]=='W') starts.add(i);
			}
			ArrayList<Segment> segs=new ArrayList<>();
			for(int leftIdx: starts) {
				int c=0,j=leftIdx;
				while(j<n && s[j++]=='L') {
					c++;
				}
				if(leftIdx+c!=n) segs.add(new Segment(leftIdx,leftIdx+c-1,c));
			}
			Collections.sort(segs);
			//for(Segment x: segs) out.println(x.l+" "+x.r+" "+x.len);
			for(Segment x: segs) {
				int r=x.r;
				while(k>0 && r>=x.l) {
					s[r--]='W';
					k--;
				}
			}
			if(k>0) {
				int i=0;
				for(;i<n;i++) {
					if(s[i]=='W') break;
				}
				for(i--;i>=0;i--) {
					if(k-->0) s[i]='W';
					else break;
				}
				i=n-1;
				for(;i>=0;i--) {
					if(s[i]=='W') break;
				}
				for(i++;i<n;i++) {
					if(k-->0) s[i]='W';
					else break;
				}
			}

			out.println(ans(s));
		}



	}

	int ans(char s[]) {
		int n=s.length;
		int cur=0,gg=0;
		int start=-1;
		for(int i=0;i<n;i++) {
			if(s[i]=='W') {
				if(cur==0) {
					cur=1;
					gg+=cur;
				}else {
					gg+=cur+1;
				}
			}else {
				cur=0;
			}
		}
		return gg;
	}

	class Segment implements Comparable<Segment> {
		int l, r, len;
		Segment () {}
		Segment (int l_, int r_, int d_) {
		    this.l = l_;
		    this.r = r_;
		    this.len = d_;
		}
		@Override
		public int compareTo(Segment o) {
		    return (int)((len - o.len) !=0 ? len-o.len : o.r - r);
		}
    }




