public class B {

	static void sort(int a[], int n) {
		if (n == 0) {
			return;
		}
		for (int i = 1; i < n; i++) {
			int j = i;
			int ca = a[i];
			do {
				int nj = (j - 1) >> 1;
				int na = a[nj];
				if (ca <= na) {
					break;
				}
				a[j] = na;
				j = nj;
			} while (j != 0);
			a[j] = ca;
		}
		int ca = a[0];
		for (int i = n - 1; i > 0; i--) {
			int j = 0;
			while ((j << 1) + 2 + Integer.MIN_VALUE < i + Integer.MIN_VALUE) {
				j <<= 1;
				j += (a[j + 2] > a[j + 1]) ? 2 : 1;
			}
			if ((j << 1) + 2 == i) {
				j = (j << 1) + 1;
			}
			int na = a[i];
			a[i] = ca;
			ca = na;
			while (j != 0 && a[j] < ca) {
				j = (j - 1) >> 1;
			}
			while (j != 0) {
				na = a[j];
				a[j] = ca;
				ca = na;
				j = (j - 1) >> 1;
			}
		}
		a[0] = ca;
	}

	static void solve() throws Exception {
		int tests = scanInt();
		for (int test = 0; test < tests; test++) {
			int n = scanInt(), k = scanInt(), gaps[] = new int[n], nGaps = 0;
			String s = scanString();
			int haveWins = 0, curGap = 0;
			for (int i = 0; i < n; i++) {
				if (s.charAt(i) == 'W') {
					++haveWins;
					if (curGap != 0 && curGap != i) {
						gaps[nGaps++] = curGap;
					}
					curGap = 0;
				} else {
					++curGap;
				}
			}
			if (haveWins + k == 0) {
				out.println(0);
			} else if (haveWins + k >= n) {
				out.println(2 * n - 1);
			} else {
				sort(gaps, nGaps);
				int closedGaps = 0;
				while (closedGaps < nGaps && k >= gaps[closedGaps]) {
					k -= gaps[closedGaps];
					haveWins += gaps[closedGaps];
					++closedGaps;
				}
				out.println(2 * (haveWins + k) - 1 - (nGaps - closedGaps));
			}
		}
	}

	static int scanInt() throws IOException {
		return parseInt(scanString());
	}

	static long scanLong() throws IOException {
		return parseLong(scanString());
	}

	static String scanString() throws IOException {
		while (tok == null || !tok.hasMoreTokens()) {
			tok = new StringTokenizer(in.readLine());
		}
		return tok.nextToken();
	}

	static BufferedReader in;
	static PrintWriter out;
	static StringTokenizer tok;

	public static void main(String[] args) {
		try {
			in = new BufferedReader(new InputStreamReader(System.in));
			out = new PrintWriter(System.out);
			solve();
			in.close();
			out.close();
		} catch (Throwable e) {
			e.printStackTrace();
			exit(1);
		}
	}
}

