    cin >> str;
    str = "L" + str + "L";
    vector<int> p1, p2, p3;
    int totw = 0;
    vector<int> wind;
    int score = 0;
    for (int i = 1; i <= n; i++) {
      if (str[i] == 'W') {
        totw++;
        score++;
        if (str[i - 1] == 'W') {
          score++;
        }
        wind.push_back(i);
      }
    }
    if (totw == 0) {
      for (int i = 1; i <= k; i++) {
        str[i] = 'W';
        score++;
        if (str[i - 1] == 'W') {
          score++;
        }
      }
    } else if (totw + k >= n) {
      for (int i = 1; i <= n; i++) {
        str[i] = 'W';
      }
      score = (n - 1) * 2 + 1;
