      vector<pair<int, int>> gaps;
      for (int i = 1; i < wind.size(); i++) {
        if (wind[i] - wind[i - 1] - 1 > 0) {
          gaps.push_back(
              {wind[i] - wind[i - 1] - 1, 2 * (wind[i] - wind[i - 1] - 1) + 1});
        }
      }
      sort(gaps.begin(), gaps.end(), customSort);
      int ind = 0;
      while (k > 0) {
        if (ind >= gaps.size()) {
          break;
        }
        int replace = gaps[ind].first;
        if (k >= replace) {
          k -= replace;
          score += gaps[ind].second;
        } else {
          score += k * 2;
          k = 0;
        }
        ind++;
      }
      if (k > 0) {
        score += 2 * k;
      }
    }
    cout << score << endl;
  }
}

