    public static void main(String[] args) throws IOException {
        FastScanner in = new FastScanner();
        MyWriter out = new MyWriter(System.out);


        int tries = in.nextInt();

        boolean[] win_lose = new boolean[100_000];

        for (int i = 0; i < tries; i++) {
            int n = in.nextInt();
            int k = in.nextInt();
            String results = in.next();

            int all_losses = 0;
            int current_win = 0;

            boolean prev = false;
            for (int j = 0; j < n; j++) {

                if (results.charAt(j) == 'W') {
                    if (prev) {
                        current_win += 2;
                    } else {
                        current_win += 1;
                    }
                    win_lose[j] = true;
                    prev = true;
                } else {
                    all_losses++;
                    win_lose[j] = false;
                    prev = false;
                }
            }
            int gain = doWork(n, k, win_lose, all_losses, current_win);

            out.print(gain + current_win);
            out.println();
        }

        out.close();
    }

    public static int doWork(int n, int k, boolean[] win_lose, int all_losses, int all_wins) {
        int additional_win = 0;

        List<Segment> segments = new ArrayList<>();
        int last_win = -1;
        for (int i = 0; i < n; i++) {
            if (win_lose[i]) {
                if (last_win != -1 && i - last_win > 1) {
                    segments.add(new Segment(last_win, i));
                }
                last_win = i;
            }
        }
        Collections.sort(segments);

        int changes_left = k;
        for (Segment segment : segments) {
            int span = segment.end - segment.start - 1;
            if (changes_left - span >= 0) {
                additional_win += (span * 2 + 1);
                all_losses -= span;
                changes_left -= span;
            } else {
                additional_win += (changes_left * 2);
                return additional_win;
            }
        }
        if (changes_left == 0) {
            return additional_win;
        }

        boolean is_there_any_W = (additional_win + all_wins != 0);
        if (is_there_any_W) {
            if (all_losses > changes_left) {
                additional_win += (changes_left * 2);
            } else {
                additional_win += (all_losses * 2);
            }
        } else {
            if (all_losses > changes_left) {
                additional_win += (changes_left * 2) - 1;
            } else {
                additional_win += (all_losses * 2) - 1;
            }
        }


        return additional_win;
    }

    public static class Segment implements Comparable<Segment> {
        public int start;
        public int end;

        public Segment(int start, int end) {
            this.start = start;
            this.end = end;
        }


        @Override
        public int compareTo(Segment o) {
            return (this.end - this.start) - (o.end - o.start);
        }
    }


