public class CFB {
  BufferedReader br;
  PrintWriter out;
  StringTokenizer st;
  boolean eof;
  private static final long MOD = 1000L * 1000L * 1000L + 7;
  private static final int[] dx = {0, -1, 0, 1};
  private static final int[] dy = {1, 0, -1, 0};
  private static final String yes = "Yes";
  private static final String no = "No";
  private static final char WIN = 'W';
  private static final char LOSE = 'L';

  void solve() {
    int T = nextInt();
    for (int i = 0; i < T; i++) {
      helper();
    }
  }

  void helper() {
    int n = nextInt();
    int k = nextInt();
    char[] arr = nextString().toCharArray();

    List<Pair<Integer, Integer>> pairs = new ArrayList<>();
    int start = 0;
    while (start < n) {
      if (arr[start] == 'W') {
        start++;
        continue;
      }

      int end = start;
      while (end < n && arr[end] == 'L') {
        end++;
      }

      pairs.add(new Pair<>(start, end - 1));
      start = end;
    }

    List<Pair<Integer, Integer>> first = new ArrayList<>();
    List<Pair<Integer, Integer>> second = new ArrayList<>();
    for (Pair<Integer, Integer> p : pairs) {
      if (p.first == 0 || p.second == n - 1) {
        second.add(p);
      } else {
        first.add(p);
      }
    }

    Collections.sort(first, new Comparator<Pair<Integer, Integer>>() {
      @Override
      public int compare(Pair<Integer, Integer> o1, Pair<Integer, Integer> o2) {
        return Integer.compare(o1.second - o1.first, o2.second - o2.first);
      }
    });
    for (Pair<Integer, Integer> p : first) {
      if (k == 0) {
        break;
      }

      int len = p.second - p.first + 1;
      int min = Math.min(k, len);
      for (int i = 0; i < min; i++) {
        arr[p.first + i] = WIN;
      }

      k -= min;
    }

    for (Pair<Integer, Integer> p : second) {
      if (k == 0) {
        break;
      }

      int len = p.second - p.first + 1;
      int min = Math.min(k, len);

      if (p.first == 0) {
        for (int i = 0; i < min; i++) {
          arr[p.second - i] = WIN;
        }
      } else {
        for (int i = 0; i < min; i++) {
          arr[p.first + i] = WIN;
        }
      }

      k -= min;
    }

    int res = 0;
    for (int i = 0; i < n; i++) {
      if (arr[i] == LOSE) {
        continue;
      }
      res++;
      if (i > 0 && arr[i - 1] == WIN) {
        res++;
      }
    }
    outln(res);
  }

  class Pair<A, B> {
    A first;
    B second;

    public Pair(A first, B second) {
      this.first = first;
      this.second = second;
    }

    public int hashCode() {
      int hashFirst = first != null ? first.hashCode() : 0;
      int hashSecond = second != null ? second.hashCode() : 0;

      return (hashFirst + hashSecond) * hashSecond + hashFirst;
    }

    public boolean equals(Object other) {
      if (other instanceof Pair) {
        Pair otherPair = (Pair) other;
        return
            ((  this.first == otherPair.first ||
                ( this.first != null && otherPair.first != null &&
                    this.first.equals(otherPair.first))) &&
                (  this.second == otherPair.second ||
                    ( this.second != null && otherPair.second != null &&
                        this.second.equals(otherPair.second))) );
      }

      return false;
    }

    public String toString() {
      return "(" + first + ", " + second + ")";
    }
  }

  void shuffle(long[] a) {
    int n = a.length;
    for(int i = 0; i < n; i++) {
        int r = i + (int) (Math.random() * (n - i));
        long tmp = a[i];
        a[i] = a[r];
        a[r] = tmp;
    }
  }
  long gcd(long a, long b) {
    while(a != 0 && b != 0) {
        long c = b;
        b = a % b;
        a = c;
    }
    return a + b;
  }
  private void outln(Object o) {
      out.println(o);
  }
  private void out(Object o) {
      out.print(o);
  }
  private void formatPrint(double val) {
      outln(String.format("%.9f%n", val));
  }
  public CFB() {
    br = new BufferedReader(new InputStreamReader(System.in));
    out = new PrintWriter(System.out);
    solve();
    out.close();
  }
  public static void main(String[] args) {
      new CFB();
  }

  public long[] nextLongArr(int n) {
    long[] res = new long[n];
    for(int i = 0; i < n; i++)
        res[i] = nextLong();
    return res;
  }
  public int[] nextIntArr(int n) {
    int[] res = new int[n];
    for(int i = 0; i < n; i++)
        res[i] = nextInt();
    return res;
  }
  public String nextToken() {
    while (st == null || !st.hasMoreTokens()) {
        try {
            st = new StringTokenizer(br.readLine());
        } catch (Exception e) {
            eof = true;
            return null;
        }
    }
    return st.nextToken();
  }
  public String nextString() {
    try {
        return br.readLine();
    } catch (IOException e) {
        eof = true;
        return null;
    }
  }
  public int nextInt() {
      return Integer.parseInt(nextToken());
  }
  public long nextLong() {
      return Long.parseLong(nextToken());
  }
  public double nextDouble() {
        return Double.parseDouble(nextToken());
    }
}

t = int(input())
for it in range(0, t):
    n, k = tuple(list(map(int, input().split(' '))))
    results = [char for char in input()]

    initial_score = 0
    loss_amount = 0

    for i in range(0, len(results)):
        if i > 0 and results[i] == 'W' and results[i - 1] == 'W':
            initial_score += 1
        if results[i] == 'L':
            loss_amount += 1

    k = min(k, loss_amount)
    initial_score += (n - loss_amount)

    streak_increase_added_score = 2 * k

    if loss_amount == n and streak_increase_added_score > 0:
        streak_increase_added_score -= 1

    streak_diffs = []
    current_streak_diff = 0
    streak_found = 0
    for i in range(0, n):
        if results[i] == 'W':
            streak_found = True
            if current_streak_diff != 0:
                streak_diffs.append(current_streak_diff)
                current_streak_diff = 0
        if results[i] == 'L' and streak_found:
            current_streak_diff += 1


    sorted_streak_diffs = sorted(streak_diffs)

    disjoint_streak_decrease_added_score = 0
    for i in range(0, len(sorted_streak_diffs)):
        if k >= sorted_streak_diffs[i]:
            disjoint_streak_decrease_added_score += 1
            k -= sorted_streak_diffs[i]
        else:
            break

    final_score = (initial_score + streak_increase_added_score + disjoint_streak_decrease_added_score)

    print(final_score)

