    static void solve(int testCnt) {
        for (int testNumber = 0; testNumber < testCnt; testNumber++) {
            int n = in.nextInt(), k = in.nextInt();
            String s = in.next();

            int sum = 0;
            int l = -1, r = -1, l1 = -1, r1 = -1, l2 = -1, r2 = -1;
            List<int[]> intervals = new ArrayList<>();
            for(int i = 0; i < n; i++) {
                if(s.charAt(i) == 'W') {
                    sum += (i > 0 && s.charAt(i - 1) == 'W' ? 2 : 1);
                }
                else {
                    if(l < 0) {
                        l = i;
                        r = i;
                    }
                    else if(i - 1 == r){
                        r = i;
                    }
                    else {
                        if(l == 0) {
                            l1 = l;
                            r1 = r;
                            l = i;
                            r = i;
                        }
                        else {
                            intervals.add(new int[]{l, r});
                            l = i;
                            r = i;
                        }
                    }
                }
            }
            if(l == 0) {
                l1 = l;
                r1 = r;
            }
            else if(l > 0) {
                if(r == n - 1) {
                    l2 = l;
                    r2 = r;
                }
                else {
                    intervals.add(new int[]{l, r});
                }
            }
            Collections.sort(intervals, (e1, e2) -> {
                return (e1[1] - e1[0]) - (e2[1] - e2[0]);
            });
            for(int i = 0; i < intervals.size() && k > 0; i++) {
                int[] curr = intervals.get(i);
                if(k >= curr[1] - curr[0] + 1) {
                    sum += (2 * (curr[1] - curr[0] + 1) + (curr[1] == n - 1 ? 0 : 1));
                }
                else {
                    sum += 2 * k;
                }
                k -= Math.min(k, curr[1] - curr[0] + 1);
            }
            if(k > 0) {
                int cnt1 = l1 >= 0 ? (r1 - l1 + 1) : 0;
                int cnt2 = l2 >= 0 ? (r2 - l2 + 1) : 0;
                if(cnt1 + cnt2 == n) {
                    sum += k * 2 - 1;
                }
                else {
                    sum += Math.min(k, cnt1 + cnt2) * 2;
                }
            }
