      if (ch == 'W') {
        if (!lastWin) {
          streak_gaps.push_back(1);
        } else
          streak_gaps[streak_gaps.size() - 1]++;
        lastWin = true;
        lastLost = false;
      } else {
        if (!lastLost) {
          streak_gaps.push_back(-1);
        } else
          streak_gaps[streak_gaps.size() - 1]--;
        lastLost = true;
        lastWin = false;
      }
    }
