    for (auto it = streak_gaps.begin(); it != streak_gaps.end(); it++) {
      if (*it > 0) {
        it = streak_gaps.erase(it);
        it--;
        streaks++;
      } else
        *it = -*it;
    }
    sort(streak_gaps.begin(), streak_gaps.end());
    int fills = 0;
