  static long powmodulo(long a, long p) {
    if (p == 0)
      return 1 % mod;
    if (p == 1)
      return a % mod;
    long ans = 1;
    while (p > 0) {
      if ((p & 1) > 0) {
        ans = (ans * a) % mod;
      }
      a = (a * a) % mod;
      p = p >> 1;
    }
    return ans % mod;
  }




  static long mod = 1000000007;

  static long gcd(long a,long b)
  {
    if(a==0)
      return b;
    return  gcd(b%a,a);
  }

  static long fast_powerNumbers(long a,long n)
  {
    if(n==1)
      return a;
    long ans=fast_powerNumbers(a,n/2);
    if(n%2==0)
      return (ans*ans);
    else
      return ((ans*ans)*(a));
  }

